(function(){
  const config = {
    ondevtoolopen: (type, next) => {
      const currentOrigin = window.location.origin;
      const redirectPath = "/aviso"; // Altere para a slug desejada
      window.location.href = currentOrigin + redirectPath;
      next();
    }
  };

  const script = document.createElement("script");
  script.src = "https://cdn.jsdelivr.net/npm/disable-devtool@latest";
  script.onload = () => window.DisableDevtool(config);

  document.head.appendChild(script);
})();
