(function () {
  const redirectTo = "/aviso"; // slug de redirecionamento
  let isRedirected = false;

  const detectDevTools = () => {
    const threshold = 160;
    const widthThreshold = 160;

    const devtoolsOpen =
      window.outerWidth - window.innerWidth > widthThreshold ||
      window.outerHeight - window.innerHeight > threshold;

    if (devtoolsOpen && !isRedirected) {
      isRedirected = true;
      const url = window.location.origin + redirectTo;
      window.location.replace(url);
    }
  };

  // Detecta imediatamente ao carregar (caso DevTools jÃ¡ esteja aberto)
  window.addEventListener("load", () => {
    detectDevTools();
    setInterval(detectDevTools, 1000); // continua monitorando depois
  });
})();
